mapper.update:
update bpmn_task_record set run_mark = 0
where proc_inst_id = #{procInstId}
and run_mark = 1

mapper.select:
select * from bpmn_task_record where db_status = 1
and proc_inst_id = #{procInstId}
and run_mark = #{runMark}
and task_id = #{taskId}
and act_inst_id = #{actInstId}

mapper.update:
UPDATE bpmn_task_record
proc_def_id = #{procDefId}, proc_inst_id = #{procInstId}, act_inst_id = #{actInstId}, task_id = #{taskId}, task_def_id = #{taskDefId}, task_name = #{taskName}, from_act_inst_id = #{fromActInstId}, from_task_id = #{fromTaskId}, from_task_def_id = #{fromTaskDefId}, from_task_name = #{fromTaskName}, run_mark = #{runMark}, assignee = #{assignee}, assignee_name = #{assigneeName}, start_time = #{startTime}, end_time = #{endTime}, duration = #{duration}, db_status = #{dbStatus}, updater = #{updater}, update_time = #{updateTime}
WHERE id = #{id}
