mapper.select:
select t1.*,t2.name as parent_name from sys_menu t1 left join sys_menu t2 on t1.parent_id = t2.id where t1.db_status = 1

	and t1.type = #{query.type}


	and t1.parent_id = #{query.parentId}


	and t1.name like concat('%', #{query.name}, '%')

order by t1.sort asc

select t3.* from sys_user_role t1 left join sys_role_menu t2 on t1.role_id = t2.role_id left join sys_menu t3 on t2.menu_id = t3.id where t1.user_id = #{userId} and t1.db_status = 1 and t2.db_status = 1 and t3.db_status = 1
and t3.type = #{type}
order by t3.sort asc

mapper.update:
UPDATE sys_menu
parent_id = #{parentId}, name = #{name}, url = #{url}, menu_path = #{menuPath}, authority = #{authority}, type = #{type}, open_style = #{openStyle}, icon = #{icon}, sort = #{sort}, db_status = #{dbStatus}, mark = #{mark}, updater = #{updater}, update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
select * from sys_menu where db_status = 1
and parent_id = #{parentId}
and menu_path = #{menuPath}
order by sort
