mapper.select:
select * from oh_project where db_status = 1
and status = #{status}
and start_time >= #{startTime}
and end_time <= #{endTime}

    and (project_name like concat('%',#{keyWord}, '%') or project_code like concat('%',#{keyWord}, '%')
    or project_alias like concat('%',#{keyWord}, '%') or director_name like concat('%',#{keyWord}, '%'))

mapper.update:
UPDATE oh_project
project_code = #{projectCode}, project_name = #{projectName}, project_alias = #{projectAlias}, start_time = #{startTime}, end_time = #{endTime}, director = #{director}, director_name = #{directorName}, status = #{status}, db_status = #{dbStatus}, updater = #{updater}, update_time = #{updateTime},
WHERE id = #{id}
