mapper.select:
select t1.*, (select t2.name from sys_dept t2 where t2.id = t1.parent_id) parentName,t3.tenant_name from sys_dept t1 left join tenant_member t3 on t1.tenant_id = t3.tenant_id where t1.db_status = 1 and t1.name like concat('%', #{name}, '%') and t3.tenant_name like concat('%', #{tenantName}, '%') and t1.parent_id = #{parentId}
and t1.id in
#{deptId}
order by t1.tenant_id,t1.sort asc
