mapper.select:
select * from sys_params where db_status = 1
and param_value = #{paramValue}
and param_key = #{paramKey}
and param_type = #{paramType}

    and id in

        #{id}


order by id desc

mapper.update:
UPDATE sys_params
param_name = #{paramName}, param_type = #{paramType}, param_key = #{paramKey}, param_value = #{paramValue}, remark = #{remark}, db_status = #{dbStatus}, updater = #{updater}, update_time = #{updateTime}
WHERE id = #{id}
