mapper.select:
select t1.*, t2.name orgName
from sys_user t1 left join sys_org t2 on t1.org_id = t2.id
where t1.super_admin = 0 and t1.db_status = 1

	and t1.username = #{params.username}


	and t1.username in

		#{item}



	and t1.mobile = #{params.mobile}


	and t1.gender = #{params.gender}


	and t1.real_name like concat("%",#{params.realName},"%")


	and t1.org_id = #{params.orgId}


	and t1.tenant_id = #{params.tenantId}


	and ifnull(t1.tenant_id, '') != #{params.unTenantId}


	and ifnull(t1.tenant_id, '') != ''


	and (t2.name like concat("%",#{params.keyWord},"%") or t1.real_name like concat("%",#{params.keyWord},"%")
	or t1.username like concat("%",#{params.keyWord},"%") or t1.mobile like concat("%",#{params.keyWord},"%")
	or t1.email like concat("%",#{params.keyWord},"%"))

order by t1.create_time desc

select t1.*,t3.name as org_name from sys_user t1 left join sys_user_role t2 on t1.id = t2.user_id
left join sys_org t3 on t1.org_id = t3.id
where t1.db_status = 1 and t2.db_status = 1 and t2.role_id = #{roleId}

	and t1.username = #{username}


	and t1.mobile = #{mobile}


	and t1.gender = #{gender}

mapper.update:
update sys_user
username = #{username}, password = #{password}, real_name = #{realName}, avatar = #{avatar}, gender = #{gender}, email = #{email}, mobile = #{mobile}, org_id = #{orgId}, super_admin = #{superAdmin}, status = #{status}, db_status = #{dbStatus}, updater = #{updater}, pwd_modify_time = #{pwdModifyTime}, user_key = #{userKey}, tenant_id = #{tenantId}, update_time = #{updateTime}
WHERE id = #{id}

update sys_user

	tenant_id = #{tenantId},
	updater = #{updater},
	update_time = #{updateTime}

WHERE id = #{id}
