mapper.update:
UPDATE data_function
name = #{name}, func_code = #{funcCode}, is_async = #{isAsync}, remark = #{remark}, updater = #{updater}, db_status = #{dbStatus}, update_time = #{updateTime}
WHERE id = #{id}

UPDATE data_function_authority
client_id = #{clientId}, func_code = #{funcCode}, remark = #{remark}, db_status = #{dbStatus}, updater = #{updater}, update_time = #{updateTime}
WHERE id = #{id}

UPDATE data_function_authority
set db_status = #{dbStatus},
    update_time = sysdate()
where client_id = #{clientId}
    and func_code = #{funcCode}
