mapper.select:
select a.*,b.name funcCodeName,c.name clientName from data_message a left join data_function b on a.func_code = b.func_code and b.db_status = 1
left join data_app c on a.client_id = c.client_id and c.db_status = 1
where a.db_status = 1
and a.client_id = #{clientId}
and a.state = #{state}
and a.func_code = #{funcCode}
and a.topic = #{topic}
and a.create_time >= #{startDate}
and a.create_time <= #{endDate}

    and (a.func_code like concat('%',#{keyWord}, '%') or a.client_id like concat('%',#{keyWord}, '%')
    or a.topic like concat('%',#{keyWord}, '%'))

order by a.create_time desc

mapper.update:
UPDATE data_message
client_id = #{clientId}, func_code = #{funcCode}, topic = #{topic}, json_str = #{jsonStr}, state = #{state}, result_msg = #{resultMsg}, note = #{note}, updater = #{updater}, db_status = #{dbStatus}, update_time = #{updateTime}
WHERE id = #{id}
