mapper.select:
select * from sms_platform where db_status = 1
and platform = #{platform}
and sign_name like concat('%',#{signName}, '%')

mapper.update:
UPDATE sms_platform
platform = #{platform}, sign_name = #{signName}, template_id = #{templateId}, app_id = #{appId}, sender_id = #{senderId}, url = #{url}, access_key = #{accessKey}, secret_key = #{secretKey}, status = #{status}, db_status = #{dbStatus}, updater = #{updater}, update_time = #{updateTime}
WHERE id = #{id}
