mapper.select:
select * from sys_post where db_status = 1
and post_code = #{postCode}
and post_name like concat('%', #{postName}, '%')
and status = #{status}
order by sort

mapper.update:
UPDATE sys_post
post_code = #{postCode}, post_name = #{postName}, sort = #{sort}, status = #{status}, db_status = #{dbStatus}, updater = #{updater}, update_time = #{updateTime}
WHERE id = #{id}
