mapper.update:
UPDATE sys_version_info
version_num = #{versionNum}, content = #{content}, release_time = #{releaseTime}, is_curr_version = #{isCurrVersion}, cover_picture = #{coverPicture}, db_status = #{dbStatus}, updater = #{updater}, update_time = #{updateTime}
WHERE id = #{id}

UPDATE sys_version_info
set is_curr_version = #{isCurrVersion}
where db_status = 1
